/*
 * @lc app=leetcode.cn id=76 lang=javascript
 *
 * [76] 最小覆盖子串
 */

// @lc code=start
/**
 * @param {string} s
 * @param {string} t
 * @return {string}
 */
var minWindow = function(s, t) {
  var left = 0;
  var right = 0;
  var missingType = 0;      // 当前缺失的字符种类数
  var map = {}; // t为baac的话，map为{a:2,b:1,c:1} 需要找齐的种类数 +1
  for (const c of t) {      // t为baac的话，map为{a:2,b:1,c:1}
    if (!map[c]) {
      missingType++;        // 需要找齐的种类数 +1
      map[c] = 1;
    } else {
      map[c]++;
    }
  }

  var res = '';

  while(right < s.length){
    var rightChar = s[right];
    right ++;

    if (map[rightChar] !== undefined) map[rightChar]--; // 是目标字符，它的缺失个数-1
    if (map[rightChar] === 0) missingType--;   // 它的缺失个数新变为0，缺失的种类数就-1

    while(missingType === 0){
      if(res === '' || res.length > s.substring(left, right).length){
        res = s.substring(left, right);
      }
      var leftChar = s[left];
      left ++;
      if (map[leftChar] !== undefined) map[leftChar]++; // 是目标字符，它的缺失个数-1
      if (map[leftChar] > 0) missingType++;   // 它的缺失个数新变为0，缺失的种类数就-1
    }
  }

  // if(left === s.length) return '';

  return res

};
// @lc code=end

console.log(minWindow('ADOBECODEBANC', 'ABC'));
console.log(minWindow('a', 'aa'));
console.log(minWindow('a', 'a'));
console.log(minWindow(
  "",
  ""
))